package org.zeromem.lifecode.algorithmaction.leetcode;

/**
 * @author zeromem
 * @date 2017/11/2
 * Given a string S, you are allowed to convert it to a palindrome by adding characters in front of it. Find and return the shortest palindrome you can find by performing this transformation.

For example:

Given "aacecaaa", return "aaacecaaa".

Given "abcd", return "dcbabcd".
 输入字符串特别长的时候会超时。

 */
public class _214ShortestPalindrome {
    public static void main(String[] args) {
        _214ShortestPalindrome test = new _214ShortestPalindrome();
        System.out.println(test.shortestPalindrome(""));
//        System.out.println(test.shortestPalindrome("aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa"));
    }

    public String shortestPalindrome(String s) {
        int n = s.length();
        if (n <= 1) {
            return s;
        }

        int temp;
        for (temp = 1; temp < n; temp++) {
            if (s.charAt(0) != s.charAt(temp)) {
                break;
            }
        }
        System.out.println(temp);
        if (temp == n) {
            return s;
        }


        boolean[][] dp = new boolean[n][n];
        for (int i = 0; i < (n + 1) / 2; i++) {
            dp[i][i] = true;
        }
        for (int i = 0; i < n / 2; i++) {
            dp[i][i + 1] = s.charAt(i) == s.charAt(i + 1);
        }

        for (int delta = 2; delta < n; delta++) {
            for (int i = 0; i < (n + 1) / 2 && i + delta < n; i++) {
                dp[i][i + delta] = dp[i + 1][i + delta - 1] && s.charAt(i) == s.charAt(i + delta);
            }
        }

        char[] prefix = new char[n - 1];
        int pos = n - 1, x = 0;
        while (pos >= 1 && !dp[0][pos]) {
            prefix[x] = s.charAt(pos);
            x++;
            pos--;
        }

        return new String(prefix, 0, x) + s;
    }
}
